import React, { useState } from 'react'

function ProductsS() {

    const [count] = useState(0) //for cart

    const Item = () => {

        const Items = () => {
            const [products, setProducts] = useState([]); //display array of objects
    
            useEffect(() => {
                fetch('http://localhost:3090/api/product_list')
                .then(res => res.json())
                .then(products => {
                    const productData = slice.map(product => (
                        <div className="col-sm-4" key={product.id}> {/* get unique id */}
    
                            <Link to={`productSingle/${product.id}`}> 
                            {/*links to product single page to display one item's info*/}
    
                                <img className='img-responsive' style={{height: '300px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={product.image} alt={product.name}/>
    
                            </Link>
                            
                            <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>{product.name}</h3>
    
                        </div>
                    ))
                    setProducts(productData)
                });
            }, []);
        };
    }

    return (
        <>
        <div style={{height: '640px', background: '#F1E7D2'}}>
            <div className="container">
                <div className='row'>
                    <div className="col-sm-6" style={{paddingTop: '8%'}}>
                        <img style={{width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src="media\sweetFillNs\mangoCake.jpg" alt="product"/>
                        <h2 style={{height: '50px', background: '#3F2A2D', color: 'white'}}>Mango cake</h2>
                    </div>
                    <div className="col-sm-6" style={{paddingTop: '15%'}}>
                        <h3>Description: </h3>
                        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Fugiat dignissimos fuga voluptatum labore esse neque ab natus in iste omnis, vitae laborum recusandae corrupti architecto repellat delectus asperiores quia saepe?</p>
                        <h3>Currently Availavle: </h3>
                        <p>3 cakes (6 pieces each)</p>
                        <button>Add to Cart</button>
                        <ul>
                            <li style={{color: 'black', fontSize: '40px', display: 'inline'}}>
                                <i className="fas fa-shopping-cart"></i>
                            </li>
                            <li style={{background: 'brown', borderRadius: '50%', fontSize: '20px', textAlign: 'center', display: 'inline'}}>
                                {count}
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
        </>
    )
}

export default ProductsS
