import React from 'react'
import { useStateValue } from '../cart/gear/state'

//Add items to cart
function AddToCart(props) {
  const [{cart, count}, dispatch] = useStateValue()

  return (
      <>
          <button
              style={props.btnStyle}

              onClick={() => {
                  let items = cart.items
                  if(props.id) {
                      items.push(props.id)
                  }

                  dispatch({
                      type: 'changeCart',
                      newCart: {items},
                      newCount: count + 1
                  })
              }}
          >
              Add to Cart
          </button>

          <ul>
              <li style={{position:'absolute', top: '10px', left: '50px', color: 'white', fontSize: '50px', display: 'inline'}}>
              <button style={{background: 'none', border: 'none'}}><i className="fas fa-shopping-cart"></i></button> 
              </li>

              <li style={{position: 'absolute', height: '30px', width: '30px', top: '5px', left: '95px', background: '#F1E7D2', border: '1px solid black', borderRadius: '50%', color: 'black', fontSize: '23px', textAlign: 'center', lineHeight: '1.3', display: 'inline'}}>
                  {count}
              </li>
          </ul>
      </>
  )
}

export default AddToCart

// dispatch calls the reducer method and updates the cart's state
