import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{cartItems}] = this.context;
                let itemCheck = []
                // let itemQty = 0
                if (cartItems !== 0) {

                    cartItems.forEach(product => {
                        itemCheck.push(product.id)
                        // itemQty = product.qty
    
                        // find all items with the corresponding id
                    })

                    let cartInventory = resultData.filter(item => {
                        itemCheck.filter(match => match === (item.id) ? {item} : null
                    )})

                    this.setState({
                        inCart: cartInventory
                    })
                }

            })
    }

    render() {


        const productSlides = this.state.inCart.map((item, id) => {
            
            return (
                <div className="container" key={id}>
                    <div className="row">
                        <div className="col-sm-12">
                            <h1>
                                {item.title}
                                {/* {this.state.qty} */}
                            </h1>
                        </div>
                    </div>
                </div>
            )
        })

        return (
            <>
                {this.state.inCart.length === 0 ? <Empty/> : <h2>{productSlides}</h2>}
            </>
        )
    }
}

export default ShowCart
