import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: [],
            loading: true
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{cart}] = this.context;
                console.log('looking for: ' + cart.items)
                // find all items with the corresponding id
                let cartInventory = resultData.filter(item => cart.items.includes(item.id) ? console.log('Found') : console.log("Not found"))  

                this.setState({
                    inCart: cartInventory,
                    loading: false
                })

            })
    }

    render() {


        // const productSlides = this.state.loading ? <Loading/> : this.state.inCart.map((item, id) => {
            
        //     return (
        //         <div className="container" key={id}>
        //             <div className="row">
        //                 <div className="col-sm-12">
        //                     <h1>
        //                         {item.title}
        //                     </h1>
        //                 </div>
        //             </div>
        //         </div>
        //     )
        // })

        return (
            <>
                <Empty/> 
            </>
        )
    }
}

export default ShowCart
