import React, {Component} from 'react'

const inputTag = {
    padding: '10px 20px', 
    borderRadius: '5px',
    border: '1px solid grey'}

const pDisplay = {
        paddingLeft: '10px',
        paddingRight: '10px',
        fontSize: '30px',
        display: 'inline'}

class Purchase extends Component {

    constructor() {
        super()
        this.state = {
            fullName: '',
            address: '',
            city_town: '',
            staProReg: '',
            zip: '',
            country: '',
            cardNum: '',
            cardType: '',
            cvv:  0,
            month: 0,
            year: 0
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)   
    }

    handleChange(event) {
        const {name, value} = event.target
        this.setState({[name] : value})
    }
    
    handleSubmit(event) {



        //check in every field is filled out
        this.state.map(check => {

            if(check) {
        
                //for card number and cvv
                let isNum = /^\d+$/.test(this.state.cardNum)
                let isCVV = /^\d+$/.test(this.state.cvv)

                if (isNum === true && isCVV === true) { //only numbers used
                    alert('Submitted')
                    
                } else {
                    alert('invalid card number')
                }

                
            }else {
                alert('Missing information')
            }
        })
        //remember to change the submit alert to a link
        event.preventDefault()
    }

    //


    render() {

        return (
            <>
                <div style={{background: '#F1E7D2'}}>
                    <div className="container">

                        <div className="row">
                            <div className="col-sm-12">

                                <h1 style={{padding: '25px 100px', fontSize: '35px'}}>
                                    We're Prepping To Ship Your Order! Just One Final Step!
                                </h1>
                                <hr style={{border: '1px solid #3F2A2D'}}/>

                            </div>
                        </div>

                        <form onSubmit={this.handleSubmit}>

                            <div className="row" style={{paddingBottom: '50px'}}>

                                <div className="col-sm-6">

                                    <h3 style={{paddingBottom: '20px'}}>Billing Address</h3>

                                    <h4 style={{paddingTop: '10px'}}>Full Name:</h4>
                                    <input 
                                        type="text" 
                                        name="fullName"
                                        value={this.state.fullName}
                                        onChange={this.handleChange}
                                        placeholder='Full Name' 
                                        style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>Address</h4>
                                    <input 
                                        type="text" 
                                        name="address"
                                        value={this.state.address}
                                        onChange={this.handleChange}
                                        placeholder='Address' style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>City/Town</h4>
                                    <input 
                                        type="text" 
                                        name="cityTown"
                                        value={this.state.city_town}
                                        onChange={this.handleChange}
                                        placeholder='Full Location' style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>State/Province/Region</h4>
                                    <input 
                                        type="text" 
                                        name="staProReg" 
                                        value={this.state.staProReg}
                                        onChange={this.handleChange}
                                        placeholder='State/Province/Region' style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>Zip</h4>
                                    <input 
                                        type="text" 
                                        name="zip"
                                        value={this.state.zip}
                                        onChange={this.handleChange}
                                        placeholder='zip code' 
                                        maxLength='12' 
                                        style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>Country</h4>
                                    <input 
                                        type="text" 
                                        name="country"
                                        value={this.state.country}
                                        onChange={this.handleChange}
                                        placeholder='country' 
                                        style={inputTag}/>
                                </div>

                                <div className="col-sm-6">

                                    <h3 style={{paddingBottom: '20px'}}>Credit Card Info</h3>

                                    <h4 style={{paddingTop: '10px'}}>Card Number</h4>
                                    <input 
                                        type="text"
                                        name="cardNum"
                                        value={this.state.cardNum}
                                        onChange={this.handleChange} 
                                        placeholder='0000 0000 0000 0000' 
                                        maxLength='16' 
                                        style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>Card Type</h4>
                                    <select 
                                        name="cardType" 
                                        value={this.state.cardType}
                                        onChange={this.handleChange}
                                        style={{padding: '10px 20px'}}>
                                            <option value="Debit">Debit</option>
                                            <option value="Credit">Credit</option>
                                            <option value="GiftC">Gift Card</option>
                                    </select>

                                    <h4 style={{paddingTop: '10px'}}>CVV</h4>
                                    <input 
                                        type="text" 
                                        name="CVV"
                                        value={this.state.cvv}
                                        onChange={this.handleChange}
                                        placeholder='123' 
                                        maxLength='3'
                                        style={inputTag}/>

                                    <h4 style={{paddingTop: '10px'}}>Expires</h4>

                                    <input 
                                        type="number" 
                                        name="month"
                                        value={this.state.month}
                                        onChange={this.handleChange}
                                        placeholder='month' 
                                        max='12' 
                                        style={inputTag}/>
                                    
                                    <p style={pDisplay}>/</p>

                                    <input 
                                        type="number" 
                                        name="year"
                                        value={this.state.year}
                                        onChange={this.handleChange}
                                        placeholder='year'
                                        max='23' 
                                        style={inputTag}/>   
                                </div>
                            </div>

                            <button>Confirm Purchase</button>

                        </form>

                        <div className="row" style={{marginBottom: '50px'}}>

                            {/* redirect to products page */}
                            <div className="col-sm-12">
                                <button style={{padding: '20px 50px', border: 'none',borderRadius: '20px', background: 'red', color: 'white', fontSize: '20px'}}>Cancel Purchase</button>
                            </div>
                            
                        </div>

                    </div>                
                </div>
            </>
        )
    }
}

//make sure that the radio button replaces the input of the 'Name On Package' when checked and clears when unchecked

//make 'onsubmit' functions

//store info when submitted in database
//record all inputted data and store them accordingly within each database section
//call the databases required to do so
//use state variables to store data

export default Purchase

