import React, { useState, useEffect } from 'react'

const ProductSetup = () => {
    return (

        <>
            <div className="col-sm-4">
                <img className='img-responsive' style={{height: '300px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src="media\sweetFillNs\berryCups.jpg" alt="imageA"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
            <div className="col-sm-4">
                    <img className='img-responsive' src="media\sweetFillNs\mangoCake.jpg" style={{height: '300px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} alt="imageB"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
            <div className="col-sm-4">
                    <img  className='img-responsive' src="media\sweetFillNs\roastedPineapple.jpg" style={{height: '300px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} alt="imageC"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
        </>

    )
}

function Products() {

    const [count] = useState(0); //cart
    //cart doesnt need a db
    //make an empty array to add and remove info from

    // const Items = () => {
    //     const [products, setProducts] = useState([]); //display list

    //     useEffect(() => {
    //         fetch('http://localhost:3090/api/product_list')
    //         .then(res => res.json())
    //         .then(products => {
    //             setProducts(products)
    //         });
    // }, [])
    // //componentDidMount is the array passed as a param



    // useEffect(() => {
    //     fetchData();
    // })

    //product image and product title will be calling from the api
    //so add a componentDidMount for fetch
    //make sure that the title and image both have variables
    //map the api to get just the title and image

    //pt2
    //when the title is clicked, the single product page will appear
    //describing only that object
    //the add to cart button increases the count next to the cart and stores
    //item name in cart list

    return (
        <>
            <div style={{height: '125px', background: '#3F2A2D', color: 'white'}}>
                <div className="container">
                    <div className="row">

                        <div className="col-sm-7">
                            <h1 style={{marginTop: '45px', marginLeft: '20px'}}>
                                Our Choice Selections From 'Location'!
                            </h1>
                        </div>

                        <div className="col-sm-3" style={{marginTop: '20px', marginLeft: '45px'}}>

                            <ul style={{marginTop: '20px', padding: '0px', listStyleType: 'none'}}>

                                <li style={{position:'absolute', top: '10px', left: '50px', color: 'white', fontSize: '50px', display: 'inline'}}>
                                   <button style={{background: 'none', border: 'none'}}><i className="fas fa-shopping-cart"></i></button> 
                                </li>

                                <li style={{position: 'absolute', height: '30px', width: '30px', top: '5px', left: '90px', background: '#F1E7D2', border: '1px solid black', borderRadius: '50%', color: 'black', fontSize: '23px', textAlign: 'center', lineHeight: '1.3', display: 'inline'}}>
                                    {count}
                                </li>

                                <li style={{marginTop: '-15px', marginLeft: '130px'}}>
                                    <button style={{padding: '15px 25px' ,background: 'white', border: 'none', borderRadius: '20px', color: 'black', fontSize: '30px', display: 'inline'}}>
                                        Checkout</button> 
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>

            <div style={{paddingBottom: '4%', background: '#F1E7D2'}}>
               <div className="container">
                   <div className="row" style={{paddingTop: '7%'}}>

                       <div className="col-sm-2">
                           <button style={{marginTop: '15px', fontSize: '40px', color: '#3F2A2D', background: 'none', border: 'none'}}>&lt;</button>
                       </div>

                        <div className="col-sm-8">
                            <h2 style={{textAlign: 'center', fontSize: '45px'}}>Lorem, ipsum.</h2>
                            <hr style={{border: '1px solid #3F2A2D'}}/>
                        </div>

                        <div className="col-sm-2">
                            <button style={{marginTop: '15px', fontSize: '40px', color: '#3F2A2D', background: 'none', border: 'none', float: 'right'}}>&gt;</button>
                        </div>
                   </div>

                   <div className="row" style={{paddingTop: '5%', paddingBottom: '5%'}}>

                    {/* call end result here */}

                    </div>

                    <div className="row">
                        <div className="col-sm-6">
                            <input type="radio" name='available' style={{height: '20px', width: '20px'}}/>
                            <p style={{paddingLeft: '20px', fontSize: '30px', display: 'inline'}}>Available</p>
                        </div>
                        <div className="col-sm-6">
                            <input type="radio" name='not_Available' style={{height: '20px', width: '20px'}}/>
                            <p style={{paddingLeft: '20px', fontSize: '30px', display: 'inline'}}>Not Available</p>
                        </div>
                    </div>
                </div>

            </div>
        </>
    )

}

export default Products


// the product list will display the first three items fromm a list of items in each category
// when the right button is clicked, the next list of items will be shown from the last index plus one
// when the left button is clicked, the previous list of items will be shown from the last index minus one
// no loops!

// when an item is clicked, the user will be transferred to the product single page
// only info related to that one product will appear, along with an 'add to cart' button and 'checkout' button
// a back button will lead them back to the previous page
