import React, { useState } from 'react'

const ItemSingle = () => {

    const [items, setItems] = useState([]); //display array of objects
    const [count] = useState(0) //for cart

    //when the title is clicked, the single product page will appear
    //describing only that object
    //the add to cart button increases the count next to the cart and stores
    //item name in cart list

    useEffect(() => {

        fetch(`http://localhost:3090/api/productSingle:${product.id}`)
            .then(res => res.json())
            .then(items => {

                const itemData = items.map(item => (
                    <div style={{height: '640px', background: '#F1E7D2'}}>

                        <div className="container">
                            
                            <div className='row'>

                                <div className="col-sm-6" style={{paddingTop: '8%'}}>

                                    <img style={{width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={item.image} alt="product"/>

                                    <h2 style={{height: '50px', background: '#3F2A2D', color: 'white'}}>
                                        {item.title}
                                    </h2>

                                </div>

                                <div className="col-sm-6" style={{paddingTop: '15%'}}>
                                    <h3>Description: </h3>

                                    <p>{item.description}</p>

                                    <button>Add to Cart</button>

                                    <ul>
                                        <li style={{color: 'black', fontSize: '40px', display: 'inline'}}>
                                            <i className="fas fa-shopping-cart"></i>
                                        </li>
                                        <li style={{background: 'brown', borderRadius: '50%', fontSize: '20px', textAlign: 'center', display: 'inline'}}>
                                            {count}
                                        </li>
                                    </ul>
                                </div>

                            </div>
                        </div>
                    </div>
                ));
                setItems(itemData);
            });

    }, [items]);

    return (
        <>
            {items}
        </>
    )
    //componentDidMount is the array passed as a param
}



function ProductsS() {

    return (
        <>
            <ItemSingle/>
        </>
    )
}

export default ProductsS
