import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

function List(props) {  
  
    const title = props.title
    const qty = props.qty
    
    return(  
        <div className="container" key={props.idx}>
            <div className="row">
                <div className="col-sm-12">
                    <h1>{title}</h1>
                    <h2>{qty}</h2>
                </div>
            </div>
        </div>
    )
  }

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: [],
            loading: true
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                // console.log(resultData[0].id)

                let cartInventory = [];

                const [{cart}] = this.context;
                for (let i = 0; i < resultData.length; i++) {
                    for (let j = 0; j < cart.length; j++) {
                        if (cart[j].id === (resultData[i].id)) {
                            cartInventory.push({title: resultData[i].title, qty: cart[j].qty})
                        }
                    }
                }

                // console.log('looking for: ' + cart.items)
                // // find all items with the corresponding id
                // let cartInventory = resultData.filter(item => cart.items.includes(item.id) ? console.log('Found') : console.log("Not found"))  

                this.setState({
                    inCart: cartInventory,
                    loading: false
                })

            })
    }

    render() {


        const productSlides = this.state.loading ? <Loading/> : this.state.inCart.map((item, idx) => {
            
            return (
                <List key={idx} {...item}/>
            )
        })

        return (
            <>
               {this.state.inCart.length === 0 ? <Empty/> : {productSlides}}
            </>
        )
    }
}

export default ShowCart
