import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useStateValue } from './gear/state'

function Empty() {

    return (

        <div style={{background: '#F1E7D2'}}>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <img className='center-block img-responsive' 
                        style={{height: '300px', maxWidth: '100%', padding: '60px'}} src="/media/shoppingBag.png" alt="sad shopping bag"/>
                        <h1 style={{marginBottom: '39px', textAlign: 'center'}}>Nothing In Cart</h1>
                    </div>
                </div> 
            </div>
        </div>
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

function List(props) {

    return (
        <>
            <p style={{fontSize: '25px'}}> 
                {props.title} 
                <span style={{marginLeft: '10%'}}>Qty: {props.qty}</span>
            </p>  
            <button onClick={() => props.handleRemove(props.title, props.qty)}>-</button>
        </>
    )
}

// function updateCount() {

//     const [{count}, dispatch] = useStateValue()

//     return(
//         <>

//         if (updateCount === 0) {
//             return updateCount
//         }

//             dispatch({
//             type: 'changeCount',
//             newCount: updateCount
//         })
//     )

//     let updateCount = count - 1

// }

function ShowCart() {

    const [{cart, count}, dispatch] = useStateValue()
    // const[ inCart ] = useState([])
    // constructor() {
    //     super()
    //     this.state = {
    //         inCart: [],
    //         loading: true
    //     }
    //     this.handleRemove = this.handleRemove.bind(this)
    // }

    // fetch database and show info that matches ids in global cart
    useEffect(() => {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                // console.log(resultData[0].id)

                let cartInventory = [];

                for (let i = 0; i < resultData.length; i++) {
                    for (let j = 0; j < cart.length; j++) {
                        if (cart[j].id === (resultData[i].id)) {
                            cartInventory.push({
                                title: resultData[i].title, 
                                price: resultData[i].price, 
                                qty: cart[j].qty
                            })
                        }
                    }
                }


                dispatch({
                    type: 'changeCart',
                    newCart: cartInventory
                })

            })
    }, {});

    // const handleRemove = (title) => {
    //     // locate qty of item called
    //     let removeItem = this.state.inCart

    //     removeItem.forEach(cartItem => {
    //         if (cartItem.title === title) {
    //             cartItem.qty -= 1;
    //         } 
    //     });

    //     let changeCart = removeItem.filter(product => product.qty > 0)
    //     // let newTotal = removeItem.price
        
    //     this.setState({
    //         inCart: changeCart
    //     })
        
    //     // updateCount() //run caount update function    
    // }
        
    // let total = 0

    const show = cart.length === 0 ? {display: 'none'} : {display: 'block'}

    // const productSlides = this.state.loading ? <Loading /> : this.state.inCart.map(
    //     (item, idx) => {

    //         total += Number(item.price)
    //         return <List 
    //         key={idx} 
    //         {...item} 
    //         handleRemove={this.handleRemove}/>
    //     }
    // )

    return (

        <>
            {cart.length === 0 ? <Empty/> :

                <div style={{height: '500px', background: '#F1E7D2'}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-6" style={{height: '350px', marginTop: '60px', overflowY: 'scroll'}}>
                                Nothing yet
                            </div>

                        </div>
                        <div className="row" style={{margin: '-80px auto', float: 'right'}}>
                            <div className="col-sm-12">
                                {/* <h2 style={{marginBottom: '30px'}}>Total: {total}</h2> */}
                                <p style={show}>
                                    <Link to='/personinfo' style={{padding: '15px 30px',borderRadius: '15px', boxShadow: '0px 5px 5px black', background: 'green', color: 'white', fontSize: '30px'}}>
                                        Checkout
                                    </Link>
                                </p>
                            </div>
                        </div>

                    </div>
                </div>
            }
        </>
    )

}

export default ShowCart
