import React from 'react'
import { Link } from 'react-router-dom'
import { useStateValue } from '../cart/gear/state'

//Add items to cart
function AddToCart(props) {
  const [{cart, count}, dispatch] = useStateValue()

  return (
      <>
          <button
              style={props.btnStyle}
              onClick={() => {
                  let cartItems = cart.items
                  let found = false; //used to check if item is new or already in cart

                  cartItems.forEach(cartItem => {
                      if (cartItem.id === Number(props.id)) {
                          cartItem.qty += 1;
                          found = true;
                      } 
                  });
              
                  if (!found) {
                    cartItems.push({
                          id: Number(props.id),
                          qty: 1
                      });
                  }
                  
                  dispatch({
                      type: 'changeCart',
                      newCart: {cartItems}
                  })

                  dispatch({
                    type: 'changeCount',
                    newCount: count + 1,
                })

              }}>
              Add to Cart
          </button>

          <ul style={{listStyleType: 'none'}}>
              <li style={{display: 'inline'}}> 
              <Link to='/cartItems' style={props.cartStyle}><i className="fas fa-shopping-cart"></i></Link> 
              </li>

              <li style={props.countStyle}>
                  {count}
              </li>
          </ul>
          
      </>
  )
}

export default AddToCart

// dispatch calls the reducer method and updates the cart's state
