import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const inputTag = {
    width: '100%',
    padding: '10px 20px', 
    borderRadius: '5px',
    border: '1px solid grey'
}
    
function Person() {

    const [submitBtn, setSubmit] = useState('block')
    const [nextBtn, setNext] = useState('none')

    const [info, setInfo] = useState({
        firstName: '',
        lastName: '',
        email: '',
        address: '',
        city_town: '',
        staProReg: '',
        country: '',
        zip: ''

    })   

    const handleChange = (event) => {
        const {name, value} = event.target
        setInfo(prevState => {
            return{...prevState, [name]: value}
        })
    }
    
    const handleSubmit = (event) =>  {

        const store = {
            'Fname': info.firstName,
            'Lname': info.firstName,
            'email': info.email,
            'st_address': info.address,
            'city': info.city_town,
            'province': info.staProReg,
            'country': info.country,
            'zip': info.zip
        };

        // event.preventDefault();
        // fetch('http://localhost:3090/api/person_info/', {
        //     method: 'POST',
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify(store),
        // })
        // .then(response => response.json())
        // .then(data => {
        //     alert('Success')
        //     console.log(data);

        // })
        // .catch(error => {
        //     alert("Error:")
        //     console.log(error);
        // })  

        setSubmit(prevType => prevType = 'none')
        setNext(prevType => prevType = 'block')        
        
    }

    return (
        <>
            <div style={{background: '#F1E7D2'}}>
                <div className="container">

                    <div className="row">
                        <div className="col-sm-12">

                            <h1 style={{marginTop: '50px', fontSize: '35px', textAlign: 'center'}}>
                                Purchase Ready! Just Fill Out The Form Below
                            </h1>
                            <hr style={{border: '1px solid #3F2A2D'}}/>

                        </div>
                    </div>

                    <form onSubmit={handleSubmit} style={{paddingBottom: '50px'}}>

                        <div className="row">

                            <div className="col-sm-4">

                                <h4 style={{paddingTop: '10px'}}>First Name:</h4>
                                <input 
                                    type="text" 
                                    name="firstName"
                                    value={info.firstName}
                                    onChange={handleChange}
                                    placeholder='First Name' 
                                    style={inputTag}/>

                            </div>

                            <div className="col-sm-4">
                                
                                <h4 style={{paddingTop: '10px'}}>Last Name:</h4>
                                <input 
                                    type="text" 
                                    name="lastName"
                                    value={info.lastName}
                                    onChange={handleChange}
                                    placeholder='Last Name' 
                                    style={inputTag}/>
                            </div>

                            <div className="col-sm-4">
                                
                                <h4 style={{paddingTop: '10px'}}>Email:</h4>
                                <input 
                                    type="text" 
                                    name="email"
                                    value={info.email}
                                    onChange={handleChange}
                                    placeholder='Email' 
                                    style={inputTag}/>
                            </div>

                        </div>

                        <div className="row">

                            <div className="col-sm-4">
                                <h4 style={{paddingTop: '10px'}}>Address</h4>
                                <input 
                                    type="text" 
                                    name="address"
                                    value={info.address}
                                    onChange={handleChange}
                                    placeholder='Address' style={inputTag}/>                                    
                            </div>

                            <div className="col-sm-4">
                                <h4 style={{paddingTop: '10px'}}>City/Town</h4>
                                <input 
                                    type="text" 
                                    name="city_town"
                                    value={info.city_town}
                                    onChange={handleChange}
                                    placeholder='Address' style={inputTag}/>                                       
                            </div>

                            <div className="col-sm-4">
                                <h4 style={{paddingTop: '10px'}}>State/Province/Region</h4>
                                <input 
                                    type="text" 
                                    name="staProReg" 
                                    value={info.staProReg}
                                    onChange={handleChange}
                                    placeholder='State/Province/Region' style={inputTag}/>                                    
                            </div>

                        </div>

                        <div className="row">

                            <div className="col-sm-4">
                                <h4 style={{paddingTop: '10px'}}>Country</h4>
                                <input 
                                    type="text" 
                                    name="country"
                                    value={info.country}
                                    onChange={handleChange}
                                    placeholder='Country' 
                                    style={inputTag}/>                                    
                            </div>

                            <div className="col-sm-2">
                                <h4 style={{paddingTop: '10px'}}>Zip</h4>
                                <input 
                                    type="text" 
                                    name="zip"
                                    value={info.zip}
                                    onChange={handleChange}
                                    placeholder='zip code' 
                                    maxLength='12' 
                                    style={inputTag}/>                                    
                            </div>
                        </div>

                        <div className="row" style={{marginTop: '50px', marginBottom: '30px'}}>
                            
                            <div className="col-sm-12" style={{display: submitBtn}}>
                                <button style={{height: '80px', width: '100%', background: '#3F2A2D', border: 'none', borderRadius: '20px', color: 'white', fontSize: '20px', textAlign: 'center', lineHeight: '4'}}>
                                        Submit
                                </button>
                            </div>

                            <div className="col-sm-12" style={{display: nextBtn}}>
                                <Link to='/cardInfo'>
                                    <button style={{height: '80px', width: '100%', background: '#3F2A2D', border: 'none', borderRadius: '20px', color: 'white', fontSize: '20px', textAlign: 'center', lineHeight: '4'}}>
                                            Continue To Next Step
                                    </button>
                                </Link>

                            </div>

                        </div>

                    </form>


                </div>                
            </div>
        </>
    )
}

export default Person
