import React, {Component} from 'react'
import { Link } from 'react-router-dom'

const inputTag = {
    width: '100%',
    padding: '10px 20px', 
    borderRadius: '5px',
    border: '1px solid grey'
}
    
class Person extends Component {

    constructor() {
        super()
        this.state = {
            firstName: '',
            lastName: '',
            address: '',
            city_town: '',
            staProReg: '',
            zip: ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)   
    }

    handleChange(event) {
        const {name, value} = event.target
        this.setState({[name] : value})
    }
    
    handleSubmit(event) {


        // fetch('http://localhost:3040/')
        //check in every field is filled out
        // this.state.map(check => {

        //     if(check) {
        
        //         //for card number and cvv
        //         let isNum = /^\d+$/.test(this.state.cardNum)
        //         let isCVV = /^\d+$/.test(this.state.cvv)

        //         if (isNum === true && isCVV === true) { //only numbers used
        //             alert('Submitted')
                    
        //         } else {
        //             alert('invalid card number')
        //         }

                
        //     }else {
        //         alert('Missing information')
        //     }

        //     return check
        // })

        // alert('form submitted')
        //remember to change the submit alert to a link
        event.preventDefault()
    }

    //


    render() {

        return (
            <>
                <div style={{background: '#F1E7D2'}}>
                    <div className="container">

                        <div className="row">
                            <div className="col-sm-12">

                                <h1 style={{marginTop: '50px', fontSize: '35px'}}>
                                    Enter Shipping Address:
                                </h1>
                                <hr style={{border: '1px solid #3F2A2D'}}/>

                            </div>
                        </div>

                        <form onSubmit={this.handleSubmit} style={{paddingBottom: '50px'}}>

                            <div className="row">

                                <div className="col-sm-6">

                                    <h4 style={{paddingTop: '10px'}}>First Name:</h4>
                                    <input 
                                        type="text" 
                                        name="firstName"
                                        value={this.state.firstName}
                                        onChange={this.handleChange}
                                        placeholder='First Name' 
                                        style={inputTag}/>

                                </div>

                                <div className="col-sm-6">
                                    
                                    <h4 style={{paddingTop: '10px'}}>Last Name:</h4>
                                    <input 
                                        type="text" 
                                        name="lastName"
                                        value={this.state.lastName}
                                        onChange={this.handleChange}
                                        placeholder='Last Name' 
                                        style={inputTag}/>
                                </div>

                            </div>

                            <div className="row">

                                <div className="col-sm-4">
                                    <h4 style={{paddingTop: '10px'}}>Address</h4>
                                    <input 
                                        type="text" 
                                        name="address"
                                        value={this.state.address}
                                        onChange={this.handleChange}
                                        placeholder='Address' style={inputTag}/>                                    
                                </div>

                                <div className="col-sm-4">
                                    <h4 style={{paddingTop: '10px'}}>City/Town</h4>
                                    <input 
                                        type="text" 
                                        name="cityTown"
                                        value={this.state.city_town}
                                        onChange={this.handleChange}
                                        placeholder='City/Town' style={inputTag}/>                                    
                                </div>

                                <div className="col-sm-4">
                                    <h4 style={{paddingTop: '10px'}}>State/Province/Region</h4>
                                    <input 
                                        type="text" 
                                        name="staProReg" 
                                        value={this.state.staProReg}
                                        onChange={this.handleChange}
                                        placeholder='State/Province/Region' style={inputTag}/>                                    
                                </div>

                            </div>

                            <div className="row">

                                <div className="col-sm-4">
                                    <h4 style={{paddingTop: '10px'}}>Country</h4>
                                    <input 
                                        type="text" 
                                        name="country"
                                        value={this.state.country}
                                        onChange={this.handleChange}
                                        placeholder='Country' 
                                        style={inputTag}/>                                    
                                </div>

                                <div className="col-sm-2">
                                    <h4 style={{paddingTop: '10px'}}>Zip</h4>
                                    <input 
                                        type="text" 
                                        name="zip"
                                        value={this.state.zip}
                                        onChange={this.handleChange}
                                        placeholder='zip code' 
                                        maxLength='12' 
                                        style={inputTag}/>                                    
                                </div>
                            </div>

                            <div className="row" style={{marginTop: '50px', marginBottom: '30px'}}>

                                {/* link to cardInfo page */}
                                <div className="col-sm-12">

                                    <div style={{height: '80px', width: '100%', background: 'red', borderRadius: '20px', textAlign: 'center', lineHeight: '4'}}>
                                        <Link to='/cardinfo' style={{color: 'white', fontSize: '20px'}}>
                                            Continue
                                        </Link>
                                    </div>
                                    
                                </div>
                                
                            </div>

                        </form>


                    </div>                
                </div>
            </>
        )
    }
}

//make sure that the radio button replaces the input of the 'Name On Package' when checked and clears when unchecked

//make 'onsubmit' functions

//store info when submitted in database
//record all inputted data and store them accordingly within each database section
//call the databases required to do so
//use state variables to store data

export default Person
