import React from 'react'
import { Link } from 'react-router-dom'
import { useStateValue } from '../cart/gear/state'

//Add items to cart
function AddToCart(props) {
  const [cart, count, dispatch] = useStateValue()

  return (
      <>
          <button
              style={props.btnStyle}

              onClick={() => {
                  let items = cart.items
                  if(props.id) {
                      items.push(props.id)
                  }

                  dispatch({
                      type: 'changeCart',
                      newCart: {items},
                      newCount: count + 1
                  })
              }}
          >
              Add to Cart
          </button>

          <ul style={{listStyleType: 'none'}}>
              <li style={{display: 'inline'}}> 
              <Link to='./cart/cartList' style={props.cartStyle}><i className="fas fa-shopping-cart"></i></Link> 
              </li>

              <li style={props.countStyle}>
                  {this.count}
              </li>
          </ul>
      </>
  )
}

export default AddToCart

// dispatch calls the reducer method and updates the cart's state
