import React, {Component} from 'react'
import Hero from './hero'

class Feedback extends Component {

    constructor() {
        super()
        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            cInput: ''
            }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)       
    }

    handleChange(event) {
        const {name, value} = event.target 
        this.setState({[name] : value})
    }

    handleSubmit() {
        alert('Form Submitted!')
    }

    render () {
        return(
            <>  
                <Hero
                    h1Style = {{
                        position: 'absolute', marginTop: '3%', marginLeft: '24%', fontSize: '50px', color: 'white', textTransform: 'capitalize', zIndex: '1'
                    }}
                    header='Where Should We Sail Next?'
                    img={'/media/download(resized).jpg'}
                />

                <div style={{height: '125px', background: '#3F2A2D'}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-12">
                                <h1 style={{padding: '25px 290px', color: 'white', fontSize: '35px'}}>
                                    Our Next Stop Could Be Up To You!
                                </h1>
                            </div>
                        </div>
                    </div>
                </div>

                <div style={{height: '650px', background: '#F1E7D2'}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-6">

                                <h2 style={{marginTop: '20%', fontSize: '35px', textDecoration: 'underline'}}>
                                    Note: Please read the About section first!!
                                </h2>
                                <p style={{marginTop: '8%', fontSize: '25px'}}>There are more places to explore and more snacks to try, and our selection is only just the beginning! Know a spot that has a delicious selection of delights? Just leave your email, location of choice, and YOUR selection of favorite treats, and we'll notify you if your location is our next stop and we may even feature your favorite treats on our main page!</p>

                            </div>

                            <div className="col-sm-6">
                                <form onSubmit={this.handleSubmit} style={{marginTop: '12%', marginLeft: '15%'}}>

                                    <h2>Name: </h2>
                                    <input
                                    style={{padding: '10px', marginRight: '20px', height: '35px', width: '190px'}}  
                                    type="text" 
                                    name="firstName"
                                    value={this.state.firstName}
                                    onChange={this.handleChange}
                                    placeholder="First Name"/>

                                    <input 
                                    style={{padding: '10px', height: '35px', width: '190px'}} 
                                    type="text" 
                                    name="lastName"
                                    value={this.state.lastName}
                                    onChange={this.handleChange}
                                    placeholder="Last Name"/>

                                    <h2>Email:</h2>
                                    <input
                                        style={{padding: '10px', height: '35px', width: '400px'}} 
                                        type="text"
                                        name='email'
                                        value={this.state.email}
                                        onChange={this.handleChange}
                                        placeholder='SweetSailsYum@gmail.com'/>

                                    <textarea 
                                        style={{marginTop: '40px', padding: '15px', width: '400px'}} 
                                        name='cInput'
                                        value={this.state.cInput}
                                        onChange={this.handleChange}
                                        placeholder="Tell us about your favorite treats here! Also include the location of said treats. Go into as much detail as you can, because the best treats have the best descriptions!"
                                        cols="30" 
                                        rows="10">
                                    </textarea>

                                    <button style={{marginTop: '10px', height: '60px', width: '400px', background: '#3F2A2D', border: 'none', borderRadius: '15px', display: 'block'}}
                                    >
                                        <p style={{marginTop: '3px', color: 'white', fontSize: '35px'}}>
                                            Submit
                                        </p>
                                    </button>
                                    
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

export default Feedback
