import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useStateValue } from '../cart/gear/state'
import RemoveFromCart from './cartRemove'

function Empty() {

    return (

        <div style={{background: '#F1E7D2'}}>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <img className='center-block img-responsive' 
                        style={{height: '300px', maxWidth: '100%', padding: '60px'}} src="/media/shoppingBag.png" alt="sad shopping bag"/>
                        <h1 style={{marginBottom: '39px', textAlign: 'center'}}>Nothing In Cart</h1>
                    </div>
                </div> 
            </div>
        </div>
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

function ShowCart() {

  const [ {cart} ] = useStateValue()
  const [ load ] = useState(false)

    // use global database to edit

    let cartInventory = []
    cart.forEach(element => {
        cartInventory.push({
            id: element.id,
            title: element.title,
            qty: element.qty
        })
    });

    // //changes local cart
    // handleRemove(title) {
    //     // locate qty of item called
    //     let removeItem = this.state.inCart

    //     removeItem.forEach(cartItem => {
    //         if (cartItem.title === title) {
    //             cartItem.qty -= 1;
    //         } 
    //     });

    //     let changeCart = removeItem.filter(product => product.qty > 0)
    //     // let newTotal = removeItem.price
        
    //     this.setState({
    //         inCart: changeCart
    //     })
        
    //     // updateCount() //run caount update function    
    // }

    const List = (props) => {

        return (
            <>
                <p style={{fontSize: '25px'}}> 
                    {props.title} 
                    <span style={{marginLeft: '10%'}}>Qty: {props.qty}</span>
                </p>  
                <RemoveFromCart id={props.id} qty={props.qty}/>
            </>
        )
    }
        
    let total = 0

    const show = cart.length === 0 ? {display: 'none'} : {display: 'block'}
    const productSlides = load ? <Loading /> : <List key={cart.id} {...cart}/>
    total += Number(cart.price)

    return (
        <>
            {cart.length === 0 ? <Empty/> :

                <div style={{height: '500px', background: '#F1E7D2'}}>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-6" style={{height: '350px', marginTop: '60px', overflowY: 'scroll'}}>
                                {productSlides}
                            </div>

                        </div>
                        <div className="row" style={{margin: '-80px auto', float: 'right'}}>
                            <div className="col-sm-12">
                                <h2 style={{marginBottom: '30px'}}>Total: {total}</h2>
                                <p style={show}>
                                    <Link to='/personinfo' style={{padding: '15px 30px',borderRadius: '15px', boxShadow: '0px 5px 5px black', background: 'green', color: 'white', fontSize: '30px'}}>
                                        Checkout
                                    </Link>
                                </p>
                            </div>
                        </div>

                    </div>
                </div>
            }
        </>
    )

}

export default ShowCart
