                                    <li style={{color: 'white', fontSize: '50px', display: 'inline'}}>
                                        <i className="fas fa-shopping-cart"></i>
                                    </li>

                                    <li style={{background: '#F1E7D2', border: '1px solid black', borderRadius: '50%', color: 'black', fontSize: '23px', textAlign: 'center', lineHeight: '1.3', display: 'inline'}}>
