import React from 'react';
import { Switch, Route } from 'react-router-dom'
import { StateProvider } from './cart/gear/state'

//pages
import Nav from './components/nav'
import Home from './components/home'
import About from './components/about'
import Products from './components/products'
import ProductsSingle from './components/productsSingle'
import Person from './components/personInfo'
import Card from './components/cardInfo'
import Confirm from './components/confirm'
import Feedback from './components/feedback'
import Footer from './components/footer'

//cart
import ShowCart from './cart/cartList'


function App() {

  const initialState = {
    count: 0,
    cart: [],
    total: 0,
  }

  const reducer = (state, action) => {
    switch (action.type) {

      case 'changeCart':
        return {
          ...state,
          cart: action.newCart,
        };

      case 'changeCount':
        return {
          ...state,
          count: action.newCount
        }

      case 'changeTotal':
        return {
          ...state,
          total: action.newTotal
        }

      default:
        return state;
    }
  };

  return (
    <>
      <StateProvider initialState={initialState} reducer={reducer}>
          <Nav/>
          <Switch>
            <Route path='/Sweet_Sailings' component={Home} exact />
            <Route path='/Sweet_Sailings/about' component={About}/>
            <Route path='/Sweet_Sailings/feedback' component={Feedback} />
            <Route path='/Sweet_Sailings/products' component={Products} exact/> 
            <Route path='/Sweet_Sailings/products/:id' component={ProductsSingle}/>
            <Route path='/Sweet_Sailings/cartItems' component={ShowCart}/>
            <Route path='/Sweet_Sailings/personInfo' component={Person} />
            <Route path='/Sweet_Sailings/cardInfo' component={Card} />
            <Route path='/Sweet_Sailings/confirm' component={Confirm}/> 
          </Switch>
          <Footer/>
      </StateProvider>
    </>
  );
}

export default App;

//the POST is for the customer input page
