import React, {Component} from 'react'
import { Link } from 'react-router-dom'
    
const reduceCardNum = {
    width: '70%',
    padding: '10px 20px', 
    borderRadius: '5px',
    border: '1px solid grey'
}
const reduceSize = {
    width: '30%',
    padding: '10px 20px', 
    borderRadius: '5px',
    border: '1px solid grey'
}

const pDisplay = {
    paddingLeft: '10px',
    paddingRight: '10px',
    fontSize: '20px',
    display: 'inline'
}

class Card extends Component {

    constructor() {
        super()
        this.state = {
            cardNum: '',
            cardType: '',
            cvv:  '',
            month: '',
            year: ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)   
    }

    handleChange(event) {
        const {name, value} = event.target
        this.setState({[name] : value})
    }
    
    handleSubmit(event) {


        // fetch('http://localhost:3040/')
        //check in every field is filled out
        // this.state.map(check => {

        //     if(check) {
        
        //         //for card number and cvv
        //         let isNum = /^\d+$/.test(this.state.cardNum)
        //         let isCVV = /^\d+$/.test(this.state.cvv)

        //         if (isNum === true && isCVV === true) { //only numbers used
        //             alert('Submitted')
                    
        //         } else {
        //             alert('invalid card number')
        //         }

                
        //     }else {
        //         alert('Missing information')
        //     }

        //     return check
        // })

        // alert('form submitted')
        //remember to change the submit alert to a link
        event.preventDefault()
    }

    //


    render() {

        return (
            <>
                <div style={{background: '#F1E7D2'}}>
                    <div className="container">

                        <div className="row">
                            <div className="col-sm-12">

                                <h1 style={{marginTop: '50px', fontSize: '35px', textAlign: 'center'}}>
                                    Almost Done! Enter Payment Method:
                                </h1>
                                <hr style={{border: '1px solid #3F2A2D'}}/>

                            </div>
                        </div>

                        <form onSubmit={this.handleSubmit} style={{paddingBottom: '50px'}}>

                            <div className="row">
                                <div className="col-sm-12">
                                    <h3 style={{paddingBottom: '20px'}}>Credit Card Info</h3>
                                </div>
                            </div>

                            <div className="row">

                                <div className="col-sm-6">
                                    <h4 style={{paddingTop: '10px'}}>Card Number</h4>
                                    <input 
                                        type="text"
                                        name="cardNum"
                                        value={this.state.cardNum}
                                        onChange={this.handleChange} 
                                        placeholder='0000 0000 0000 0000' 
                                        maxLength='16' 
                                        style={reduceCardNum}/>
                                </div>

                                <div className="col-sm-6">

                                    <h4 style={{paddingTop: '10px'}}>Expires</h4>

                                    <input 
                                        type="number" 
                                        name="month"
                                        value={this.state.month}
                                        onChange={this.handleChange}
                                        placeholder='month'
                                        min='1' 
                                        max='12' 
                                        style={reduceSize}/>
                                    
                                    <p style={pDisplay}>/</p>

                                    <input 
                                        type="number" 
                                        name="year"
                                        value={this.state.year}
                                        onChange={this.handleChange}
                                        placeholder='year'
                                        min='20'
                                        max='23' 
                                        style={reduceSize}/>   
                                </div>

                            </div>

                            <div className="row">
                                <div className="col-sm-6">

                                    <h4 style={{paddingTop: '10px'}}>CVV</h4>
                                    <input 
                                        type="text" 
                                        name="CVV"
                                        value={this.state.cvv}
                                        onChange={this.handleChange}
                                        placeholder='123' 
                                        maxLength='3'
                                        style={reduceSize}/>
                                </div>

                                <div className="col-sm-6">
                                    <h4 style={{paddingTop: '10px'}}>Card Type</h4>
                                    <select 
                                        name="cardType" 
                                        value={this.state.cardType}
                                        onChange={this.handleChange}
                                        style={{padding: '10px 20px'}}>
                                            <option value="Debit">Debit</option>
                                            <option value="Credit">Credit</option>
                                            <option value="GiftC">Gift Card</option>
                                    </select>                                    
                                </div>

                            </div>

                            <div className="row" style={{marginTop: '50px', marginBottom: '30px'}}>

                                {/* redirect to products page */}
                                <div className="col-sm-6">

                                    <div style={{height: '80px', width: '250px', background: 'red', borderRadius: '20px', textAlign: 'center', lineHeight: '4'}}>
                                        <Link to='/products' style={{color: 'white', fontSize: '20px'}}>Cancel Purchase</Link>
                                    </div>
                                    
                                </div>

                                <div className="col-sm-6">

                                    <button style={{height: '80px', width: '250px', borderRadius: '20px', background: 'green', color: 'white', fontSize: '20px'}}>Confirm Purchase</button>

                                </div>
                                
                            </div>

                        </form>


                    </div>                
                </div>
            </>
        )
    }
}

export default Card
