import React, {Component} from 'react'

function Slide(props) {
    return (
        <div className="col-sm-12" key={props.idx}>
            <ul>
                <li>
                    <h1>{props.item.title}</h1>
                </li>
                <li>
                    <p>{props.item.price}</p>
                </li>
            </ul>
        </div>
    )
}

class ShowCart extends Component {

    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product/")
            .then(result => result.json())
            .then(resultData => {

                const [{ cart }] = this.context;
                let cartItems = resultData.filter(item => cart.items.includes(item.id) ? item : null)

                this.setState({
                    inCart: cartItems
                })

            })
    }

    render() {
        const productSlides = this.state.inCart.map(
            (item, idx) => {
                return <Slide key={idx} {...item}/>
            }
        )

        return (
            {productSlides}
        )
    }

