import React, {Component} from 'react'
import { Link } from 'react-router-dom'
import { StateContext } from './gear/state'
import RemoveFromCart from './cartRemove'

function Empty() {
    return (
        <div style={{background: '#F1E7D2'}}>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <img className='center-block img-responsive' 
                        style={{height: '300px', maxWidth: '100%', padding: '60px'}} src="/media/shoppingBag.png" alt="sad shopping bag"/>
                        <h1 style={{marginBottom: '39px', textAlign: 'center'}}>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </div>
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

function List(props) {

    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-12">
                    <h1>{props.title}</h1>
                    <h2>{props.qty}</h2>
                    <h3>{props.id}</h3>
                </div>
            </div>
            <Link to='/personinfo'>Checkout</Link>
            <RemoveFromCart {...props}/>
        </div>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: [],
            loading: true
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                // console.log(resultData[0].id)

                let cartInventory = [];

                const [{cart}] = this.context;
                for (let i = 0; i < resultData.length; i++) {
                    for (let j = 0; j < cart.length; j++) {
                        if (cart[j].id === (resultData[i].id)) {
                            cartInventory.push({
                                title: resultData[i].title, 
                                price: resultData[i].price, 
                                qty: cart[j].qty
                            })
                        }
                    }
                }

                this.setState({
                    inCart: cartInventory,
                    loading: false
                })

            })
    }

    render() {
        
        const productSlides = this.state.loading ? <Loading /> : this.state.inCart.map(
            (item, idx) => {
                return <List key={idx} {...item} />
            }
        )

        return (
            <>
               {this.state.inCart.length === 0 ? <Empty/>: productSlides}
            </>
        )
    }
}

export default ShowCart
