import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Slide(props) {
    console.log(props)
    return (
        <div className="col-sm-12">
            <ul>
                <li>
                    <h1>{props.item.title}</h1>
                </li>
                <li>
                    <p>{props.item.price}</p>
                </li>
            </ul>
        </div>
    )
}

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}


class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{ cart }] = this.context;
                console.log(cart.cartItems);
                let cartInventory = resultData.filter(item => cart.cartItems.includes(item.id) ? item : null)

                this.setState({
                    inCart: cartInventory
                })

            })
    }

    render() {
        
        const productSlides = this.state.inCart.map((item, idx) => {
                return <Slide key={idx} {...item}/>
            }
        )

        return (
            <>
                {this.state.inCart ? {productSlides} : <Empty/>}
            </>
        )
    }

    //   const cartItems = cart.map(items => (
    //       <div key={items.id}>
    //           <h1>{items.name}</h1>
    //           <h2>{items.price}</h2>
    //       </div>
    //   ))

    //   return (
    //       <>
    //         <div className="container">
    //             <div className="row">
    //                 <div className="col-sm-12">
    //                     <ul>
    //                         <li>{cartItems}</li>
    //                     </ul>
    //                 </div>
    //             </div>
    //         </div>
    //       </>
    //   )
}

export default ShowCart
