import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import AddToCart from '../cart/cardAdd'
import Hero from './hero'

function ProductSetup() {

    const [type, setType] = useState('large')
    const [products, setProducts] = useState([]); //display array of objects
    // const [loading, setLoading] = useState(true);
    //cart doesnt need a db
    //make an empty array to add and remove info from
  
    useEffect(() => {

        fetch(`http://localhost:3090/api/product_list/types/${type}`)
            .then(res => res.json())
            .then(products => {

                const productData = products.map(product => (

                    <div className="col-sm-2 col-lg-4" key={product.id} style={{paddingBottom: '50px'}}> 
                    {/* get unique id */}

                        <img className='img-responsive' style={{height: '300px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={product.image} alt={product.title}/>

                        <Link to={`/products/${product.id}`}> 
                        {/*links to product single page to display one item's info*/}

                            <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>{product.title}</h3>

                        </Link>

                    </div>
                ));
                setProducts(productData);
            });

    }, [type]);

    const LargePro = () => {
        setType(prevType => prevType = 'large')
    }

    const SmallPro = () => {
        setType(prevType => prevType = 'small')
    }

    const Drinks = () => {
        setType(prevType => prevType = 'drinks')
    }

    return (
        <>
            <Hero
                h1Style = {{
                    position: 'absolute', marginTop: '3%', marginLeft: '16%', fontSize: '50px', color: 'white', textTransform: 'capitalize', zIndex: '1'
                }}
                header='From Exotic Fruits To Exotic Desserts'
                img={'/media/sweetFillNs/exotic-combo.jpg'}
            />

            <div style={{height: '125px', background: '#3F2A2D', color: 'white'}}>

                <div className="container">
                    <div className="row">
                        <div className="col-sm-7">
                            <h1 style={{marginTop: '45px', textAlign: 'center'}}>
                                Browse Our Finest Selections!
                            </h1>
                        </div>

                        <div className="col-sm-3" style={{marginTop: '20px', marginLeft: '45px'}}>

                            <ul style={{marginTop: '20px', padding: '0px', listStyleType: 'none'}}>
                                
                                <li>
                                    <AddToCart 
                                        btnStyle={{display: 'none'}}
                                        cartStyle={{position:'absolute', top: '10px', left: '50px', color: 'white', fontSize: '50px'}}
                                        countStyle={{position: 'absolute', height: '30px', width: '30px', top: '5px', left: '95px', background: '#F1E7D2', border: '1px solid black', borderRadius: '50%', color: 'black', fontSize: '23px', textAlign: 'center', lineHeight: '1.3', display: 'inline'}}
                                        />
                                </li>

                                <li style={{marginTop: '0px', marginLeft: '130px'}}>
                                    
                                    <p style={{padding: '15px 25px', color: 'white', fontSize: '30px', display: 'inline'}}>
                                        In Cart: 
                                    </p> 

                                </li>

                            </ul>
                        </div>
                    </div>
                </div>
            </div>

            <div style={{paddingBottom: '4%', background: '#F1E7D2'}}>
               <div className="container">
                   <div className="row" style={{paddingTop: '7%'}}>

                       <div className="col-sm-12">
                           <ul style={{padding: '0px'}}>
                               <li style={{display: 'inline'}}>
                                   <button onClick={LargePro} style={{marginTop: '30px', marginLeft: '25px', fontSize: '40px', color: '#3F2A2D', background: 'none', border: 'none'}}>Desserts</button>
                                </li>

                               <li style={{display: 'inline'}}>
                                    <button onClick={SmallPro} style={{marginTop: '30px', marginLeft: '28%', marginRight:'30%', fontSize: '40px', color: '#3F2A2D', background: 'none', border: 'none'}}>Treats</button>
                               </li>

                               <li style={{display: 'inline'}}>
                                    <button onClick={Drinks} style={{marginTop: '30px', fontSize: '40px', color: '#3F2A2D', background: 'none', border: 'none'}}>Drinks</button>
                               </li>
                           </ul>

                           <hr style={{border: '1px solid #3F2A2D'}}/>

                       </div>
                   </div>

                   <div className="row" style={{paddingTop: '5%'}}>
                        {products}
                    </div>

                </div>

            </div>
        </>
    )
    //componentDidMount is the array passed as a param
}

export default ProductSetup


// the product list will display the first three items fromm a list of items in each category
// when the right button is clicked, the next list of items will be shown from the last index plus one
// when the left button is clicked, the previous list of items will be shown from the last index minus one
// no loops!

// when an item is clicked, the user will be transferred to the product single page
// only info related to that one product will appear, along with an 'add to cart' button and 'checkout' button
// a back button will lead them back to the previous page
