import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{cart}] = this.context;

                // find all items with the corresponding id
                let cartItems = cart.map(product => (product.id))
                let cartInventory = resultData.filter(item => cartItems.includes(item.id) ? item : null)

                // console.log('cart inventory ' + cartInventory)

                
                // resultData.filter(item => {
                //     for (let i = 0; i < cart.length; i++) {
                //         if (cart[i].id === (item.id)) {
                //             cartInventory = {item}
                //         }
                //     }
                // })

                // this.setState({
                //     inCart: cartInventory
                // })

                // console.log(cartItems)
                // console.log(cartInventory)
   

                this.setState({
                    inCart: cartInventory
                })

            })
    }

    render() {


        const productSlides = this.state.inCart.map((item, id) => {
            
            return (
                <div className="container" key={id}>
                    <div className="row">
                        <div className="col-sm-12">
                            <h1>
                                {item.title}
                            </h1>
                        </div>
                    </div>
                </div>
            )
        })

        return (
            <>
                {this.state.inCart.length === 0 ? <Empty/> : <h2>{productSlides}</h2>}
            </>
        )
    }
}

export default ShowCart
