import React, { useState, useEffect } from 'react'

const ItemSingle = () => {

    const [count] = useState(0) //for cart
    const [item, setItem] = useState([]); //display array of objects

    useEffect(() => {

        fetch('http://localhost:3090/api/product_list/')
            .then(res => res.json())
            .then(item => {

                const itemData = item.map(items => (
                    <>
                        <div className="col-sm-6" style={{paddingTop: '8%'}}>
                            <img style={{width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={items.image} alt="product"/>

                            <h2 style={{height: '50px', background: '#3F2A2D', color: 'white'}}>
                                {items.title}
                            </h2>

                        </div>

                        <div className="col-sm-6" style={{paddingTop: '15%'}}>

                            <h3>Description: </h3>
                            <p>{items.description}</p>
                            <button>Add to Cart</button>

                            <ul>
                                <li style={{color: 'black', fontSize: '40px', display: 'inline'}}>
                                    <i className="fas fa-shopping-cart"></i>
                                </li>
                                <li style={{background: 'brown', borderRadius: '50%', fontSize: '20px', textAlign: 'center', display: 'inline'}}>
                                    {count}
                                </li>
                            </ul>

                        </div>
                    </>
                ));
                setItem(itemData);
            });

    }, [item]);

    return (
        <>
            {item}
        </>
    )
    //componentDidMount is the array passed as a param
}



function ProductsS() {

    return (
        <>
        <div style={{height: '640px', background: '#F1E7D2'}}>
            <div className="container">
                <div className='row'>
                    <ItemSingle/>
                </div>
            </div>
        </div>
        </>
    )
}

export default ProductsS
