import React from 'react'
import { Link } from 'react-router-dom'
import { useStateValue } from '../cart/gear/state'

//Add items to cart
function AddToCart(props) {
  const [{cartItems, count}, dispatch] = useStateValue()

  let qty = 0

  return (
      <>
          <button
              style={props.btnStyle}

              onClick={() => {
                  let items = cartItems
                  if(props.id) {
                      qty += 1
                      items.push({
                          id: Number(props.id),
                          amount: qty})
                  }
                  
                  dispatch({
                      type: 'changeCart',
                      newCart: {items},
                      newCount: count + 1
                  })

              }}>
              Add to Cart
          </button>

          <ul style={{listStyleType: 'none'}}>
              <li style={{display: 'inline'}}> 
              <Link to='/cartItems' style={props.cartStyle}><i className="fas fa-shopping-cart"></i></Link> 
              </li>

              <li style={props.countStyle}>
                  {count}
              </li>
          </ul>
          
      </>
  )
}

export default AddToCart

// dispatch calls the reducer method and updates the cart's state
