import React, { useState, useEffect } from 'react'

const ProductsS = (props) => {

    const [count] = useState(0) //for cart
    const [item, setItem] = useState([]); //display array of objects
    const {id} = props.match.params

    useEffect(() => {

        fetch(`http://localhost:3090/api/product_list/id/${id}`)
            .then(res => res.json())
            .then(item => {

                const itemData = (

                    <>
                        <div className="container">
                            <div className="row">

                                <div className="col-sm-6" style={{paddingTop: '8%'}}>
                                    <img className='img-responsive' style={{height: '400px', width: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={item.image} alt="product"/>

                                    <h2 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '1.5'}}>
                                        {item.title}
                                    </h2>
                                    
                                </div>

                                <div className="col-sm-6" style={{paddingTop: '15%'}}>

                                    <h2 style={{fontSize: '35px'}}>Description: </h2>
                                    <p style={{fontSize: '20px'}}>{item.description}</p>

                                    <ul>
                                        <button style={{marginTop: '130px', padding: '20px 100px', borderRadius: '30px', border:'none', fontSize: '20px', background: 'brown', color: 'white'}}>Add to Cart</button>
                                        <li style={{color: 'black', fontSize: '40px', display: 'inline'}}>
                                            <i className="fas fa-shopping-cart"></i>
                                        </li>
                                        <li style={{background: 'brown', borderRadius: '50%', fontSize: '20px', textAlign: 'center', display: 'inline'}}>
                                            {count}
                                        </li>
                                    </ul>
                                </div>
                                
                            </div>
                        </div>
                    </> 


                );
                setItem(itemData);
            });

    }, [item, id, count]);

    return (
        <>
            <div style={{height: '640px', background: '#F1E7D2'}}>
                <div className="container">      
                    <div className='row'>
                         {item}
                    </div>
                </div>
            </div>
        </>
    )
    //componentDidMount is the array passed as a param
}

export default ProductsS
