import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{cartItems}] = this.context;
                let itemCheck = 0
                let itemQty = 0

                cartItems.forEach(product => {
                    itemCheck = product.id
                    itemQty = product.qty
                    return [itemCheck, itemQty]; //check
                })

                // find all items with the corresponding id
                let cartInventory = resultData.filter(item => itemCheck === (item.id) ? [{item}, itemQty] : null)



                this.setState({
                    inCart: cartInventory,
                })

            })
    }

    render() {


        const productSlides = this.state.inCart.map((item, itemQty, id) => {
            
            return (
                <div className="container" key={id}>
                    <div className="row">
                        <div className="col-sm-12">
                            <h1>
                                {item.title}
                                {item.itemQty}
                            </h1>
                        </div>
                    </div>
                </div>
            )
        })

        return (
            <>
                {this.state.inCart.length === 0 ? <Empty/> : <h2>{productSlides}</h2>}
            </>
        )
    }
}

export default ShowCart
