import React from 'react'
import { useStateValue } from '../cart/gear/state'

//Remove items to cart
function RemoveFromCart(props) {
  const [{cart, count}, dispatch] = useStateValue()

  return (
      <>
          <button
              style={{height: '50px'}}

              onClick={() => {
                  let items = cart

                  items.forEach(cartItem => {
                      if (cartItem.id === Number(props.id)) {
                          cartItem.qty -= 1;
                      } 

                      if (cartItem.qty === 0) {
                          cartItem.splice(cartItem.id, 1)
                      }
                  });

                  dispatch({
                      type: 'changeCart',
                      newCart: items,
                      newCount: count - 1,
                  })

              }}>
              -
          </button>
          
      </>
  )
}

export default RemoveFromCart

// dispatch calls the reducer method and updates the cart's state
