import React from 'react';
import { Switch, Route } from 'react-router-dom'
import { StateProvider } from './cart/gear/state'

import Nav from './components/nav'
import Home from './components/home'
import About from './components/about'
import Products from './components/products'
import ProductsSingle from './components/productsSingle'
import ShowCart from './cart/cartList'
import Purchase from './components/purchase'
import Feedback from './components/feedback'
import Footer from './components/footer'

function App() {

  const initialState = {
    count: 0,
    cart: []
  }

  const reducer = (state, action) => {
    switch (action.type) {

      case 'changeCart':
        return {
          ...state,
          cart: action.newCart,
        };

      case 'changeCount':
        return {
          ...state,
          count: action.newCount
        }

      default:
        return state;
    }
  };

  return (
    <>
      <StateProvider initialState={initialState} reducer={reducer}>
          <Nav/>
          <Switch>
            <Route path='/' component={Home} exact />
            <Route path='/about' component={About}/>
            <Route path='/feedback' component={Feedback} />
            <Route path='/products' component={Products} exact/> 
            <Route path='/products/:id' component={ProductsSingle}/>
            <Route path='/cartItems' component={ShowCart}/>
            <Route path='/purchase' component={Purchase} />
          </Switch>
          <Footer/>
      </StateProvider>
    </>
  );
}

export default App;

//the POST is for the customer input page
