import React, {Component} from 'react'
import { Link } from 'react-router-dom'
import { StateContext } from './gear/state'

function Empty() {
    return (
        <div className="row">
            <div className="col-sm-12">
                <img className='center-block img-responsive' 
                style={{height: '300px', maxWidth: '100%', padding: '60px'}} src="/media/shoppingBag.png" alt="sad shopping bag"/>
                <h1 style={{marginBottom: '39px', textAlign: 'center'}}>Nothing In Cart</h1>
            </div>
        </div> 
    )
}

function Loading() {
    return (
        <>
            <h2>Loading...</h2>
        </>
    )
}

function List(props) {

    return (
        <div className="container" style={{paddingTop: '20px'}}>
            <div className="row">
                <div className="col-sm-12">
                    <h1 style={{padding: '10px 20px', width: '60%', background: 'white', borderRadius: '15px', boxShadow: '0px 5px 5px black'}}>
                        {props.title} 
                        <span style={{marginLeft: '10%'}}>Qty: {props.qty}</span>
                    </h1>
                </div>
            </div>
            {/* <button onClick={props.handleRemove}>Remove</button> */}
        </div>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: [],
            loading: true
        }
        // this.handleRemove = this.handleRemove.bind(this)
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                // console.log(resultData[0].id)

                let cartInventory = [];

                const [{cart}] = this.context;
                for (let i = 0; i < resultData.length; i++) {
                    for (let j = 0; j < cart.length; j++) {
                        if (cart[j].id === (resultData[i].id)) {
                            cartInventory.push({
                                title: resultData[i].title, 
                                price: resultData[i].price, 
                                qty: cart[j].qty
                            })
                        }
                    }
                }

                this.setState({
                    inCart: cartInventory,
                    loading: false
                })

            })
    }

    // handleRemove() {
    //     let items = this.state.inCart
    //     console.log(items)

    //     items.forEach(cartItem => {
    //         if (cartItem.title === cartItem.title) {
    //             items.qty -= 1;
    //         } 

    //         // if (cartItem.qty === 0) {
    //         //     return items.filter(product => product.qty > 0)
    //         // }
    //     });
    // }

    render() {
        
        const show = this.state.inCart.length === 0 ? {display: 'none'} : {display: 'block'}

        const productSlides = this.state.loading ? <Loading /> : this.state.inCart.map(
            (item, idx) => {
                return <List 
                key={idx} 
                {...item} 
                handleRemove={this.handleRemove}/>
            }
        )

        return (
            <div style={{background: '#F1E7D2'}}>
                <div className="container">
                    {this.state.inCart.length === 0 ? <Empty/> : productSlides}

                    <div className="row">
                        <div className="col-sm-12">
                            <p style={show}>
                                <Link to='/personinfo' style={{position: 'absolute', right: '5%', padding: '15px 30px',borderRadius: '15px', boxShadow: '0px 5px 5px black', background: 'green', color: 'white', fontSize: '30px'}}>
                                    Checkout
                                </Link>
                            </p>
                        </div>
                    </div>

                </div>

            </div>
        )
    }
}

export default ShowCart
