import React, {Component} from 'react'
import { StateContext } from './gear/state'

function Slide() {
    return (
        <div className="col-sm-12">
            <ul>
                <li>
                    <h1>{item.title}</h1>
                </li>
                <li>
                    <p>{item.price}</p>
                </li>
            </ul>
        </div>
    )
}

function Empty() {
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <h1>Nothing In Cart</h1>
                    </div>
                </div>
            </div>
        </>
    )
}

class ShowCart extends Component {

    static contextType = StateContext;
    constructor() {
        super()
        this.state = {
            inCart: []
        }
    }

    // fetch database and show info that matches ids in global cart
    componentDidMount() {

        fetch("http://localhost:3090/api/product_list/")
            .then(result => result.json())
            .then(resultData => {

                const [{cartItems}] = this.context;
                console.log(cartItems);
                // find all items with the corresponding id
                let cartInventory = resultData.filter(item => cartItems.includes(item.id) ? {item} : console.log(false))

                this.setState({
                    inCart: cartInventory
                })
                console.log(cartInventory)

            })
    }

    render() {
        
        const productSlides = this.state.inCart.map((item, id) => {
                return <Slide key={id} {...item}/>
            }
        )

        return (
            <>
                {productSlides}
            </>
        )
    }
}

export default ShowCart
