import React, { useState, useEffect } from 'react'
import { useStateValue } from './cart/gear/state'
import { Link } from 'react-router-dom'

//Add items to cart
function AddToCart(props) {
    const [{cart}, dispatch] = useStateValue()

    return (
        <button
            style={{marginTop: '40px', marginLeft: '90px', padding: '15px 75px', borderRadius: '30px', border:'none', fontSize: '25px', background: '#3F2A2D', color: 'white'}}

            onClick={() => {
                let items = cart.items
                if(props.id) {
                    items.push(props.id)
                }

                {/*/calls the reducer method and updates the cart's state */}
                dispatch({
                    type: 'changeCart',
                    newCart: {items}
                })
            }}
        >
            Add to Cart
        </button>
    )
}

//Display Product
function ProductsSingle(props) {

    const [count] = useState(0) //for cart
    const [item, setItem] = useState([]); //display array of objects
    const {id} = props.match.params

    useEffect(() => {

        fetch(`http://localhost:3090/api/product_list/id/${id}`)
            .then(res => res.json())
            .then(item => {

                const itemData = (
        
                    <div className="row">

                        <div className="col-sm-6" style={{paddingTop: '5%', paddingBottom: '2%'}}>

                            <img className='img-responsive' style={{marginBottom: '40px', height: '400px', maxWidth: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={item.image} alt="product"/>                          

                            <h2 style={{margin: '25px 0px', padding: '10px 0px', height: '70px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '1.5'}}>
                                {item.title}
                            </h2>
  
                        </div>

                        <div className="col-sm-6" style={{paddingTop: '10%', paddingLeft: '5%'}}>

                            <h2 style={{fontSize: '35px'}}>Description: </h2>
                            <p style={{fontSize: '20px'}}>{item.description}</p>
                            <p style={{fontSize: '18px'}}>Price: {item.price}</p>

                            <AddToCart id={ProductsSingle.id}/>

                        </div>
                    </div>
        
                );

                setItem(itemData);
            });

    }, [id, count]);

    return (
        <>
            <div className="div" style={{background: '#F1E7D2'}}>
                <div className="container">
                    {item}
                </div>
            </div>

            <div style={{background: '#3F2A2D'}}>
                <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                            <Link to='/products'><h2 style={{height: '50px', fontSize: '30px', color: 'white', textAlign: 'center', lineHeight: '1.5'}}>Back To Products</h2></Link>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
    //componentDidMount is the array passed as a param
}

export default ProductsSingle
