import React, { useState } from 'react'

const ProductSetup = () => {
    return (

        <div className="row">
            <div className="col-sm-12">
                <h2 style={{textAlign: 'center'}}>Lorem, ipsum.</h2>
                <hr/>
            </div>
            <button>left</button>
            <div className="col-sm-4">
                <img className='img-responsive' style={{height: '300px', width: '100%'}} src="media\sweetFillNs\berryCups.jpg" alt="imageA"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
            <div className="col-sm-4">
                 <img src="media\sweetFillNs\mangoCake.jpg" style={{height: '300px', width: '100%'}} alt="imageB"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
            <div className="col-sm-4">
                 <img src="media\sweetFillNs\roastedPineapple.jpg" style={{height: '300px', width: '100%'}} alt="imageC"/>
                <h3 style={{height: '50px', background: '#3F2A2D', color: 'white', textAlign: 'center', lineHeight: '2'}}>Lorem.</h3>
            </div>
            <button>right</button>
        </div>
    )
}

function Products() {
    
    const [count, setCount] = useState(0)

    // const Shop = (props) => {
    //     return (
    //      <div>    
    //         <div className='listSetup'>    
