    //             <ul>
    //               <li><h1>{props.item.name}</h1></li>
    //               <li><h2>Price: ${props.item.price}</h2></li>
    //               <li>Qty: {props.item.qty}</li>
    //             </ul>
    //             <ul className='buttons'>
    //               <li><button onClick={() => props.handleItemsUp(props.item.name, props.item.price)}>+</button></li>
    //               <li><button onClick={() => props.handleItemsDown(props.item.name, props.item.price)}>-</button></li>
    //           </ul>  
    //           </div>
    //         </div>
    //      </div>
    //     )
    // }

    return (
        <>
            <div style={{height: '125px', background: '#3F2A2D', color: 'white'}}>
                <div className="container">
                    <div className="row">

                        <div className="col-sm-7">
                            <h1 style={{marginTop: '45px', marginLeft: '20px'}}>
                                Our Choice Selections From 'Location'!
                            </h1>
                        </div>

                        <div className="col-sm-3" style={{marginTop: '20px', marginLeft: '100px', height: '80px', width: '250px', background: 'white', color: 'black', borderRadius: '10px'}}>

                            <ul style={{marginTop: '20px', padding: '0px', listStyleType: 'none'}}>

                                <li style={{position:'absolute', top: '15px', left: '25px', fontSize: '40px', display: 'inline'}}>
                                    <i className="fas fa-shopping-cart"></i>
                                </li>

                                <li style={{position: 'absolute', height: '25px',width: '25px', top: '15px', left: '60px', background: 'brown', borderRadius: '50%', fontSize: '20px', textAlign: 'center', display: 'inline'}}>
                                    {count}
                                </li>

                                <li style={{marginLeft: '85px', fontSize: '30px', display: 'inline'}}>
                                    Checkout
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>

            <div style={{background: '#F1E7D2'}}>
               <div className="container">
                    <ProductSetup/>
                    <ProductSetup/>
                    <ProductSetup/>
                </div>
            </div>

            <div style={{height: '80px', background: '#3F2A2D', color: 'white'}}>
                <div className="container">
                    <div className="row">

                        <div className="col-sm-6" style={{marginTop: '20px'}}>

                            <button style={{position: 'absolute', left: '0px', height: '35px'}}>prev</button>
                            <h2 style={{paddingLeft: '50px', fontSize: '35px', display: 'inline'}}>Prev Page</h2>

                        </div>

                        <div className="col-sm-6" style={{marginTop: '20px', textAlign: 'right'}}>
                            
                            <h2 style={{paddingRight: '50px', fontSize: '35px', display: 'inline'}}>Next Page</h2>
                            <button style={{position: 'absolute', right: '0px', height: '35px'}}>next</button>

                        </div>
                    </div>
                </div>
            </div>
        </>
    )

}

export default Products


