import React, { useState, useEffect } from 'react'
import { useStateValue } from '../cart/gear/state'
import { Link } from 'react-router-dom'

//Add items to cart
function AddToCart(props) {
    const [{cart, count}, dispatch] = useStateValue()

    return (

        <>
            <button
                style={{marginTop: '40px', marginLeft: '90px', padding: '15px 75px', borderRadius: '30px', border:'none', fontSize: '25px', background: '#3F2A2D', color: 'white'}}

                onClick={() => {
                    let items = cart.items
                    if(props.id) {
                        items.push(props.id)
                    }

                    dispatch({
                        type: 'changeCart',
                        newCart: {items},
                        newCount: count + 1
                    })
                }}
            >
                Add to Cart
            </button>

            <ul>
                <li style={{position:'absolute', top: '10px', left: '50px', color: 'white', fontSize: '50px', display: 'inline'}}>
                <button style={{background: 'none', border: 'none'}}><i className="fas fa-shopping-cart"></i></button> 
                </li>

                <li style={{position: 'absolute', height: '30px', width: '30px', top: '5px', left: '95px', background: '#F1E7D2', border: '1px solid black', borderRadius: '50%', color: 'black', fontSize: '23px', textAlign: 'center', lineHeight: '1.3', display: 'inline'}}>
                    {this.count}
                </li>
            </ul>
        </>
    )
}
// dispatch calls the reducer method and updates the cart's state 

//Display Product
function ProductsSingle(props) {

    const [item, setItem] = useState([]); //display array of objects
    const {id} = props.match.params

    useEffect(() => {

        fetch(`http://localhost:3090/api/product_list/id/${id}`)
            .then(res => res.json())
            .then(item => {

                const itemData = (
        
                    <div className="row">

                        <div className="col-sm-6" style={{paddingTop: '5%', paddingBottom: '2%'}}>

                            <h2 style={{mheight: '70px', color: '#3F2A2D', textAlign: 'center', lineHeight: '1.5'}}>
                                {item.title}
                            </h2>
                            <hr style={{marginTop: '0px', marginBottom: '40px', border: '1px solid black'}}/>

                            <img className='center-block img-responsive' style={{marginBottom: '40px', height: '400px', maxWidth: '100%', border: '10px solid white', boxShadow: '1px 1px 10px black'}} src={item.image} alt="product"/>                          
  
                        </div>

                        <div className="col-sm-6" style={{paddingTop: '10%', paddingLeft: '5%'}}>

                            <h2 style={{fontSize: '35px'}}>Description: </h2>
                            <p style={{fontSize: '20px'}}>{item.description}</p>
                            <p style={{fontSize: '18px'}}>Price: {item.price}</p>

                            <AddToCart id={ProductsSingle.id}/>

                        </div>
                    </div>
        
                );

                setItem(itemData);
            });

    }, [id]);

    return (
        <>
            <div className="div" style={{background: '#F1E7D2'}}>
                <div className="container">
                    {item}
                </div>
            </div>

            <div style={{background: '#3F2A2D'}}>
                <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                            <Link to='/products'><h2 style={{height: '50px', fontSize: '30px', color: 'white', textAlign: 'center', lineHeight: '1.5'}}>Back To Products</h2></Link>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
    //componentDidMount is the array passed as a param
}

export default ProductsSingle
